// Compose Function 

const { compose } = require('redux');



function removeSpace(string) {
    return string.split(" ").join("");
}

function repeatString(string) {
    return string+string;
}

function toUpperCase(string) {
    return string.toUpperCase(); 
}

let string = " my name anubhaw singh i am a eng...";
const result1 = removeSpace(string);
const result2 = repeatString(result1);
const result3 = toUpperCase(result2);
console.log(result1, result2, result3);

// o/p 
// mynameanubhawsinghiamaeng... mynameanubhawsinghiamaeng...mynameanubhawsinghiamaeng... MYNAMEANUBHAWSINGHIAMAENG...MYNAMEANUBHAWSINGHIAMAENG...

console.log(toUpperCase(repeatString(removeSpace(string)))); // same o/p 

const composedFunction = compose(removeSpace, removeSpace, toUpperCase);
console.log(composedFunction(string)); // same output
 

// f(x) g(x) h(x) -> f(g(h(x))); 
